      overlay: false, // Disable the runtime error overlay
    },
    fs: {
      strict: true,
      deny: ["**/.*"],
    },
  },
});

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
const PORT = process.env.PORT || 5000;
const HOST = process.env.HOST || "0.0.0.0";

app.use(express.json());
app.use(express.urlencoded({ extended: false }));

// Simple logging middleware for any requests
app.use((req, res, next) => {
  const start = Date.now();
  res.on("finish", () => {
    const duration = Date.now() - start;
    if (req.path.startsWith("/api")) {
      log(`${req.method} ${req.path} ${res.statusCode} in ${duration}ms`);
    }
  });
  next();
});

(async () => {
  // Completely client-side app - no API routes needed
  // All data is handled with dummy data in the frontend

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";
    res.status(status).json({ message });
    throw err;
  });

  const port = parseInt(process.env.PORT || '5000', 10);
  const server = app.listen(port, HOST, () => {
    log(`WiFi Self-Care Platform serving on port ${port}`);
  });

  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }
})();

server/routes.ts.content:
import type { Express } from "express";
import { createServer, type Server } from "http";
import { z } from "zod";

// Local schema definitions for server-side validation
const loginSchema = z.object({
  email: z.string().email(),
  password: z.string().min(1),
});

export async function registerRoutes(app: Express): Promise<Server> {
  // Simple health check endpoint
  app.get("/api/health", (req, res) => {
    res.json({ status: "ok", message: "Server is running" });
  });

  // Authentication - Dummy login for testing only
  app.post("/api/auth/login", async (req, res) => {
    try {
      const { email, password } = loginSchema.parse(req.body);

      // Dummy users for testing
      const dummyUsers = {
        "admin@company.com": {
          id: 1,
          email: "admin@company.com",
          username: "admin",
          role: "super-admin" as const,
          password: "password123"
        },
        "manager@company.com": {
          id: 2,
          email: "manager@company.com",
          username: "manager", 
          role: "manager" as const,
          password: "password123"
        },
        "staff@company.com": {
          id: 3,
          email: "staff@company.com",
          username: "staff",
          role: "admin" as const,
          password: "password123"
        }
      };

      const user = dummyUsers[email as keyof typeof dummyUsers];
      if (!user || user.password !== password) {
        return res.status(401).json({ message: "Invalid credentials" });
      }

      // Return user without password
      const { password: _, ...userWithoutPassword } = user;
      res.json({ 
        user: userWithoutPassword,
        token: "dummy-token-123"
      });
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid input", errors: error.errors });
      }
      res.status(500).json({ message: "Internal server error" });
    }
  });

  // All other endpoints return dummy data - app is completely client-side
  app.get("/api/*", (req, res) => {
    res.json({ message: "This app uses local data only", data: [] });
  });

  app.post("/api/*", (req, res) => {
    res.json({ message: "This app uses local data only", success: true });
  });

  app.put("/api/*", (req, res) => {
    res.json({ message: "This app uses local data only", success: true });
  });

  app.delete("/api/*", (req, res) => {
    res.json({ message: "This app uses local data only", success: true });
  });

  const httpServer = createServer(app);
  return httpServer;
}

