/* Beautiful Dashboard Styles */
@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-background text-foreground;
    font-family: 'Inter', sans-serif;
  }
}

@layer components {
  .dashboard-hero {
    @apply relative overflow-hidden;
    background: linear-gradient(135deg, 
      hsl(var(--primary) / 0.1) 0%, 
      hsl(var(--primary) / 0.05) 25%, 
      transparent 50%, 
      hsl(var(--accent) / 0.05) 75%, 
      hsl(var(--accent) / 0.1) 100%
    );
  }

  .dashboard-hero::before {
    content: '';
    @apply absolute inset-0;
    background: radial-gradient(circle at 30% 20%, hsl(var(--primary) / 0.1) 0%, transparent 50%),
                radial-gradient(circle at 70% 80%, hsl(var(--accent) / 0.1) 0%, transparent 50%);
    animation: float 20s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px) rotate(0deg); }
    50% { transform: translateY(-20px) rotate(1deg); }
  }

  .stats-card {
    @apply relative bg-card backdrop-blur-lg border border-border rounded-xl shadow-lg hover:shadow-xl transition-all duration-200 ease-in-out;
    background: linear-gradient(135deg, 
      hsl(var(--card) / 0.9) 0%, 
      hsl(var(--card) / 0.7) 100%
    );
    transform: translateZ(0); /* Force GPU acceleration for smooth transitions */
    will-change: transform, box-shadow; /* Optimize for animations */
  }

  .stats-card:hover {
    transform: translateZ(0) translateY(-2px); /* Subtle lift effect without layout shift */
  }

  .stats-card::before {
    content: '';
    @apply absolute inset-0 rounded-xl opacity-0 transition-opacity duration-300;
    background: linear-gradient(135deg, 
      hsl(var(--primary) / 0.1) 0%, 
      hsl(var(--accent) / 0.05) 100%
    );
  }

  .stats-card:hover::before {
    @apply opacity-100;
  }

  .crypto-card {
    @apply relative bg-card backdrop-blur-lg border rounded-xl shadow-2xl hover:shadow-2xl transition-all duration-300 ease-in-out;
    background: linear-gradient(135deg, 
      hsl(var(--card) / 0.9) 0%, 
      hsl(var(--card) / 0.7) 50%,
      hsl(var(--primary) / 0.05) 100%
    );
    border: 1px solid hsl(var(--border) / 0.5);
    transform: translateZ(0); /* Force GPU acceleration for smooth transitions */
    will-change: transform, box-shadow; /* Optimize for animations */
  }

  .crypto-card:hover {
    transform: translateZ(0) translateY(-1px); /* Subtle lift effect without layout shift */
  }

  .crypto-card::before {
    content: '';
    @apply absolute inset-0 rounded-xl opacity-0 transition-opacity duration-500;
    background: linear-gradient(135deg, 
      hsl(var(--primary) / 0.1) 0%, 
      transparent 50%,
      hsl(var(--accent) / 0.1) 100%
    );
  }

  .crypto-card:hover::before {
    @apply opacity-100;
  }

  .text-gradient {
    background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--primary) / 0.8) 50%, hsl(var(--accent-foreground)) 100%);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
  }

  .icon-glow {
    @apply drop-shadow-lg;
    filter: drop-shadow(0 0 8px hsl(var(--primary) / 0.3));
  }

  .neon-glow {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3),
                0 0 40px hsl(var(--primary) / 0.2),
                0 0 60px hsl(var(--primary) / 0.1);
  }

  .pulse-glow {
    animation: pulse-glow 2s ease-in-out infinite;
  }

  @keyframes pulse-glow {
    0%, 100% { 
      box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
    }
    50% { 
      box-shadow: 0 0 30px hsl(var(--primary) / 0.5),
                  0 0 40px hsl(var(--primary) / 0.3);
    }
  }

  .sparkle-animation {
    animation: sparkle 3s ease-in-out infinite;
  }

  @keyframes sparkle {
    0%, 100% { opacity: 0.7; transform: scale(1); }
    50% { opacity: 1; transform: scale(1.1); }
  }

  .gradient-border {
    @apply relative overflow-hidden;
  }

  .gradient-border::before {
    content: '';
    @apply absolute inset-0;
    background: linear-gradient(45deg, 
      hsl(var(--primary)) 0%, 
      hsl(var(--accent)) 50%, 
      hsl(var(--primary)) 100%
    );
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    padding: 1px;
  }

  .admin-badge {
    @apply inline-flex items-center px-3 py-1 rounded-full text-xs font-medium;
    background: linear-gradient(135deg, 
      hsl(var(--primary) / 0.1) 0%, 
      hsl(var(--primary) / 0.2) 100%
    );
    border: 1px solid hsl(var(--primary) / 0.3);
    color: hsl(var(--primary));
  }

  .manager-badge {
    @apply inline-flex items-center px-3 py-1 rounded-full text-xs font-medium;
    background: linear-gradient(135deg, 
      hsl(var(--accent) / 0.1) 0%, 
      hsl(var(--accent) / 0.2) 100%
    );
    border: 1px solid hsl(var(--accent) / 0.3);
    color: hsl(var(--accent));
  }

  .super-admin-badge {
    @apply inline-flex items-center px-3 py-1 rounded-full text-xs font-medium;
    background: linear-gradient(135deg, 
      #ffd700 0%, 
      #ffed4a 100%
    );
    border: 1px solid #ffd700;
    color: #8b5a00;
  }

  .floating-element {
    animation: float-gentle 6s ease-in-out infinite;
  }

  @keyframes float-gentle {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .rainbow-border {
    @apply relative;
  }

  .rainbow-border::before {
    content: '';
    @apply absolute inset-0 rounded-xl;
    background: linear-gradient(45deg, 
      #ff0000, #ff7700, #ffdd00, #00ff00, 
      #0088ff, #0000ff, #8800ff, #ff0088
    );
    background-size: 400% 400%;
    animation: rainbow-rotate 4s ease-in-out infinite;
    mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    mask-composite: exclude;
    -webkit-mask: linear-gradient(#fff 0 0) content-box, linear-gradient(#fff 0 0);
    -webkit-mask-composite: xor;
    padding: 2px;
  }

  @keyframes rainbow-rotate {
    0% { background-position: 0% 50%; }
    50% { background-position: 100% 50%; }
    100% { background-position: 0% 50%; }
  }
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  }
}

@layer components {
  .gradient-bg {
    background: linear-gradient(135deg, hsl(var(--background)) 0%, hsl(var(--muted)) 100%);
  }

  .glass-effect {
    backdrop-filter: blur(10px);
    background: hsl(var(--card) / 0.8);
    border: 1px solid hsl(var(--border) / 0.5);
  }

  .neon-glow {
    box-shadow: 0 0 20px hsl(var(--primary) / 0.3);
  }

  .crypto-card {
    background: linear-gradient(135deg, hsl(var(--card)) 0%, hsl(var(--muted)) 100%);
    border: 1px solid hsl(var(--primary) / 0.2);
  }

  .stats-card {
    @apply relative overflow-hidden rounded-xl border bg-card text-card-foreground shadow-sm;
    transition: all 0.3s ease;
  }

  .stats-card:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px hsl(var(--primary) / 0.1);
  }

  .sidebar-gradient {
    background: var(--sidebar-bg);
  }

  .animate-float {
    animation: float 6s ease-in-out infinite;
  }

  @keyframes float {
    0%, 100% { transform: translateY(0px); }
    50% { transform: translateY(-10px); }
  }

  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }


}

@layer base {
  h1, h2, h3, h4, h5, h6 {
    @apply font-semibold;
  }
}

@layer utilities {
  .text-balance {
    text-wrap: balance;
