// Product Management Interfaces
export interface Product {
  id: number;
  name: string;
  model: string;
  brand: string;
  category: 'modem' | 'router' | 'cable' | 'antenna' | 'adapter' | 'switch' | 'extender' | 'fiber-cable' | 'power-supply';
  productType: 'user-sale' | 'engineer-only';
  description: string;
  specifications: {
    [key: string]: string;
  };
  price: number;
  stock: number;
  lowStockThreshold: number;
  status: 'active' | 'inactive' | 'discontinued';
  images: string[];
  warranty: string;
  supplier: string;
  supplierContact: string;
  location: string;
  createdAt: string;
  updatedAt: string;
}

export interface Order {
  id: number;
  orderNumber: string;
  productId: number;
  productName: string;
  customerId?: number;
  customerName?: string;
  engineerId?: number;
  engineerName?: string;
  orderType: 'user-purchase' | 'engineer-request' | 'stock-replenishment';
  quantity: number;
  unitPrice: number;
  totalAmount: number;
  status: 'pending' | 'approved' | 'shipped' | 'delivered' | 'cancelled';
  priority: 'low' | 'medium' | 'high' | 'urgent';
  deliveryAddress: string;
  notes?: string;
  createdAt: string;
  updatedAt: string;
  deliveredAt?: string;
}

export interface ProductFeedback {
  id: number;
  productId: number;
  productName: string;
  customerId?: number;
  customerName?: string;
  engineerId?: number;
  engineerName?: string;
  rating: number;
  title: string;
  comment: string;
  pros: string[];
  cons: string[];
  wouldRecommend: boolean;
  verifiedPurchase: boolean;
  helpfulVotes: number;
  createdAt: string;
}

