                        </DialogHeader>
                        <div className="grid grid-cols-2 gap-4">
                          <div>
                            <Label>Full Name</Label>
                            <Input placeholder="Enter customer name" />
                          </div>
                          <div>
                            <Label>Phone Number</Label>
                            <Input placeholder="+91 98765 43210" />
                          </div>
