                                    <Eye className="h-4 w-4" />
                                  </Button>
                                </DialogTrigger>
                                <DialogContent className="max-w-2xl">
                                  <DialogHeader>
                                    <DialogTitle>Lead Details - {lead.name}</DialogTitle>
                                    <DialogDescription>
                                      View and manage lead information
                                    </DialogDescription>
                                  </DialogHeader>
                                  <div className="grid grid-cols-2 gap-4">
                                    <div>
                                      <Label>Name</Label>
                                      <p className="text-sm">{lead.name}</p>
                                    </div>
                                    <div>
                                      <Label>Phone</Label>
                                      <p className="text-sm">{lead.phone}</p>
                                    </div>
                                    <div>
                                      <Label>Email</Label>
                                      <p className="text-sm">{lead.email || "Not provided"}</p>
                                    </div>
                                    <div>
                                      <Label>Address</Label>
                                      <p className="text-sm">{lead.address}</p>
                                    </div>
                                    <div>
                                      <Label>Source</Label>
                                      <p className="text-sm capitalize">{lead.source.replace('_', ' ')}</p>
                                    </div>
                                    <div>
                                      <Label>Status</Label>
                                      <p className="text-sm">{lead.status}</p>
                                    </div>
                                    <div className="col-span-2">
                                      <Label>Message</Label>
                                      <p className="text-sm">{lead.message || "No message provided"}</p>
                                    </div>
                                  </div>
                                  <div className="flex gap-2 pt-4">
