            </DialogHeader>
            <div className="space-y-6">
              {/* Plan Type Selection */}
              <div>
                <Label className="dashboard-welcome-text text-lg font-semibold">Plan Type</Label>
                <Tabs value={selectedPlanTypeForAdd} onValueChange={setSelectedPlanTypeForAdd} className="mt-2">
