    backdrop-filter: blur(20px);
  }
}

.stats-card {
  position: relative;
  overflow: hidden;
  border-radius: var(--radius);
  border: 1px solid hsl(var(--border));
  background: hsl(var(--card));
  color: hsl(var(--card-foreground));
  box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
  transition: all 0.3s ease;
}

.stats-card:hover {
  transform: translateY(-2px);
  box-shadow: 0 10px 25px hsla(var(--primary), 0.1);
}

.sidebar-gradient {
  background: var(--sidebar-bg);
}

.animate-float {
  animation: float 6s ease-in-out infinite;
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-10px); }
}

.animate-pulse-slow {
  animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

.text-gradient {
  background: linear-gradient(135deg, hsl(var(--primary)) 0%, hsl(var(--accent-foreground)) 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
}

/* Dashboard specific styles */
.dashboard-hero {
  background: var(--dashboard-hero-bg);
  color: var(--dashboard-text);
  min-height: 100vh;
  transition: all 0.3s ease;
}

.dashboard-stats-card {
  background: var(--stats-card-bg);
  border: 1px solid var(--stats-card-border);
  transition: all 0.3s ease;
}

.dashboard-stats-card:hover {
  background: var(--stats-card-hover);
  transform: translateY(-2px);
  box-shadow: 0 10px 25px hsla(var(--primary), 0.15);
}

.dashboard-chart-card {
  background: var(--chart-bg);
  border: 1px solid var(--chart-border);
  transition: all 0.3s ease;
}

.dashboard-welcome-text {
  color: var(--dashboard-text);
}

.dashboard-welcome-muted {
  color: var(--dashboard-text-muted);
}

.dashboard-welcome-icon {
  color: var(--welcome-icon);
}

.badge-super-admin {
  background: var(--badge-super-admin);
  color: white;
}

.badge-admin {
  background: var(--badge-admin);
  color: white;
}

.badge-manager {
  background: var(--badge-manager);
  color: white;
}

.sparkle-animation {
  animation: sparkle 2s infinite;
}

@keyframes sparkle {
  0%, 100% { transform: scale(1) rotate(0deg); }
  25% { transform: scale(1.1) rotate(90deg); }
  50% { transform: scale(1) rotate(180deg); }
  75% { transform: scale(1.1) rotate(270deg); }
}

.rainbow-border {
  position: relative;
  border: 2px solid transparent;
  background: var(--stats-card-bg);
}

.rainbow-border::before {
  content: '';
  position: absolute;
  top: -2px;
  left: -2px;
  right: -2px;
  bottom: -2px;
  background: linear-gradient(45deg, 
    var(--badge-super-admin), 
    var(--badge-admin), 
    var(--badge-manager), 
    var(--welcome-icon)
  );
  border-radius: inherit;
  z-index: -1;
  background-size: 200% 200%;
  animation: gradient-shift 3s ease infinite;
}

@keyframes gradient-shift {
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
}

/* Theme specific glow effects */
.crypto .dashboard-stats-card {
  box-shadow: 0 0 15px hsla(179, 82%, 51%, 0.1);
}

.crypto .dashboard-stats-card:hover {
  box-shadow: 0 0 25px hsla(179, 82%, 51%, 0.2);
}

.neon .dashboard-stats-card {
  box-shadow: 0 0 15px hsla(300, 100%, 50%, 0.1);
}

.neon .dashboard-stats-card:hover {
  box-shadow: 0 0 25px hsla(300, 100%, 50%, 0.2);
}

