import { useState } from "react";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { MapPin, Clock, Users, AlertTriangle } from "lucide-react";

interface HeatmapChartProps {
  title: string;
}

const generateHeatmapData = (type: string) => {
  const hours = Array.from({ length: 24 }, (_, i) => i);
  const days = ['Mon', 'Tue', 'Wed', 'Thu', 'Fri', 'Sat', 'Sun'];

  return days.map(day => ({
    day,
    hours: hours.map(hour => {
      let intensity;

      if (type === "complaints") {
        // More complaints during business hours and evenings
        intensity = hour >= 9 && hour <= 17 ? 0.7 + Math.random() * 0.3 :
                   hour >= 18 && hour <= 22 ? 0.5 + Math.random() * 0.4 :
                   0.1 + Math.random() * 0.3;
      } else if (type === "traffic") {
        // Higher network usage during peak hours
        intensity = hour >= 19 && hour <= 23 ? 0.8 + Math.random() * 0.2 :
                   hour >= 8 && hour <= 10 ? 0.6 + Math.random() * 0.3 :
                   hour >= 12 && hour <= 14 ? 0.5 + Math.random() * 0.3 :
                   0.2 + Math.random() * 0.3;
      } else {
        // Engineer activity during work hours
        intensity = hour >= 8 && hour <= 18 ? 0.8 + Math.random() * 0.2 :
                   hour >= 19 && hour <= 21 ? 0.3 + Math.random() * 0.3 :
                   0.05 + Math.random() * 0.15;
      }

      // Weekend adjustments
      if (day === 'Sat' || day === 'Sun') {
        intensity *= type === "complaints" ? 0.6 : type === "traffic" ? 1.2 : 0.3;
      }

      return {
        hour,
        intensity: Math.min(1, intensity),
        value: Math.floor(intensity * 100)
      };
    })
  }));
};

const generateRegionalData = () => {
  const regions = [
    { name: 'North District', complaints: 45, customers: 850, satisfaction: 4.2 },
    { name: 'South District', complaints: 32, customers: 720, satisfaction: 4.5 },
    { name: 'East District', complaints: 28, customers: 640, satisfaction: 4.6 },
    { name: 'West District', complaints: 51, customers: 920, satisfaction: 4.1 },
    { name: 'Central District', complaints: 38, customers: 780, satisfaction: 4.4 },
    { name: 'Suburban Area', complaints: 22, customers: 560, satisfaction: 4.7 },
    { name: 'Industrial Zone', complaints: 42, customers: 480, satisfaction: 4.0 },
    { name: 'Commercial Area', complaints: 35, customers: 690, satisfaction: 4.3 },
  ];

  return regions.map(region => ({
    ...region,
    complaintRate: ((region.complaints / region.customers) * 100).toFixed(2),
    efficiency: (region.satisfaction / 5 * 100).toFixed(1)
  }));
};

export function HeatmapChart({ title }: HeatmapChartProps) {
  const [dataType, setDataType] = useState("complaints");
  const [viewMode, setViewMode] = useState("heatmap");
  const [isLoading, setIsLoading] = useState(false);

  const heatmapData = generateHeatmapData(dataType);
  const regionalData = generateRegionalData();

  const handleDataTypeChange = (newType: string) => {
    setIsLoading(true);
    setDataType(newType);
    setTimeout(() => setIsLoading(false), 300);
  };

  const handleViewModeChange = (newMode: string) => {
    setIsLoading(true);
    setViewMode(newMode);
    setTimeout(() => setIsLoading(false), 200);
  };

  const getIntensityColor = (intensity: number) => {
    if (intensity < 0.2) return 'bg-green-100 text-green-800';
    if (intensity < 0.4) return 'bg-yellow-100 text-yellow-800';
    if (intensity < 0.6) return 'bg-orange-100 text-orange-800';
    if (intensity < 0.8) return 'bg-red-100 text-red-800';
    return 'bg-red-200 text-red-900';
  };

  const getRegionColor = (rate: number) => {
    if (rate < 3) return 'bg-green-100 text-green-800 border-green-200';
    if (rate < 5) return 'bg-yellow-100 text-yellow-800 border-yellow-200';
    if (rate < 7) return 'bg-orange-100 text-orange-800 border-orange-200';
    return 'bg-red-100 text-red-800 border-red-200';
  };

  const renderHeatmap = () => (
    <div className="w-full overflow-x-auto">
      <div className="min-w-[800px]">
        {/* Hour headers */}
        <div className="flex">
          <div className="w-16 text-xs text-muted-foreground font-medium py-2"></div>
          {Array.from({ length: 24 }, (_, i) => (
            <div key={i} className="flex-1 text-xs text-muted-foreground font-medium text-center py-2 min-w-8">
              {i.toString().padStart(2, '0')}
            </div>
          ))}
        </div>

        {/* Heatmap grid */}
        {heatmapData.map((dayData) => (
          <div key={dayData.day} className="flex items-center">
            <div className="w-16 text-xs text-muted-foreground font-medium py-1">
              {dayData.day}
            </div>
            {dayData.hours.map((hourData) => (
              <div
                key={hourData.hour}
                className={`flex-1 min-w-8 h-8 m-0.5 rounded text-xs flex items-center justify-center font-medium transition-all duration-200 hover:scale-110 cursor-pointer ${getIntensityColor(hourData.intensity)}`}
                title={`${dayData.day} ${hourData.hour}:00 - ${dataType === 'complaints' ? 'Complaints' : dataType === 'traffic' ? 'Traffic' : 'Activity'}: ${hourData.value}%`}
              >
                {hourData.value}
              </div>
            ))}
          </div>
        ))}
      </div>
    </div>
  );

  const renderRegionalView = () => (
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-4">
      {regionalData.map((region) => (
        <div
          key={region.name}
          className={`p-4 rounded-lg border-2 transition-all duration-200 hover:shadow-lg cursor-pointer ${getRegionColor(Number(region.complaintRate))}`}
        >
          <div className="flex items-center justify-between mb-2">
            <h4 className="font-semibold text-sm">{region.name}</h4>
            <MapPin className="h-4 w-4" />
          </div>
          <div className="space-y-1 text-xs">
            <div className="flex justify-between">
              <span>Customers:</span>
              <span className="font-medium">{region.customers.toLocaleString()}</span>
            </div>
            <div className="flex justify-between">
              <span>Complaints:</span>
              <span className="font-medium">{region.complaints}</span>
            </div>
            <div className="flex justify-between">
              <span>Rate:</span>
              <span className="font-medium">{region.complaintRate}%</span>
            </div>
            <div className="flex justify-between">
              <span>Satisfaction:</span>
              <span className="font-medium">{region.satisfaction}/5</span>
            </div>
          </div>
        </div>
      ))}
    </div>
  );

  const getDataTypeMetrics = () => {
    if (dataType === "complaints") {
      return {
        peak: "18:00-22:00",
        low: "02:00-06:00", 
        avg: "45",
        unit: "complaints/day"
      };
    } else if (dataType === "traffic") {
      return {
        peak: "19:00-23:00",
        low: "03:00-07:00",
        avg: "78",
        unit: "% utilization"
      };
    } else {
      return {
        peak: "09:00-17:00",
        low: "22:00-08:00",
        avg: "89",
        unit: "% efficiency"
      };
    }
  };

  const metrics = getDataTypeMetrics();

  return (
    <div className="w-full">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-foreground">{title}</h3>
        <div className="flex items-center gap-3">
          <Select value={viewMode} onValueChange={handleViewModeChange} disabled={isLoading}>
            <SelectTrigger className="w-[120px] h-8 text-sm border-border/50 hover:border-border transition-colors">
              <SelectValue placeholder="View mode" />
            </SelectTrigger>
            <SelectContent className="min-w-[120px]">
              <SelectItem value="heatmap">Heatmap</SelectItem>
              <SelectItem value="regional">Regional</SelectItem>
            </SelectContent>
          </Select>
          {viewMode === "heatmap" && (
            <Select value={dataType} onValueChange={handleDataTypeChange} disabled={isLoading}>
              <SelectTrigger className="w-[120px] h-8 text-sm border-border/50 hover:border-border transition-colors">
                <SelectValue placeholder="Data type" />
              </SelectTrigger>
              <SelectContent className="min-w-[120px]">
                <SelectItem value="complaints">Complaints</SelectItem>
                <SelectItem value="traffic">Traffic</SelectItem>
                <SelectItem value="activity">Activity</SelectItem>
              </SelectContent>
            </Select>
          )}
        </div>
      </div>

      <div className="relative">
        {isLoading && (
          <div className="absolute inset-0 bg-background/50 flex items-center justify-center z-10">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin"></div>
              <span className="text-sm text-muted-foreground">Loading...</span>
            </div>
          </div>
        )}

        <div className="bg-card rounded-lg border p-4">
          {viewMode === "heatmap" ? renderHeatmap() : renderRegionalView()}
        </div>
      </div>

      {/* Summary */}
      <div className="mt-4 flex items-center justify-between text-sm text-muted-foreground">
        {viewMode === "heatmap" ? (
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-1">
              <Clock className="h-4 w-4 text-red-500" />
              <span>Peak: {metrics.peak}</span>
            </div>
            <div className="flex items-center gap-1">
              <Users className="h-4 w-4 text-green-500" />
              <span>Low: {metrics.low}</span>
            </div>
            <div className="flex items-center gap-1">
              <AlertTriangle className="h-4 w-4 text-blue-500" />
              <span>Avg: {metrics.avg} {metrics.unit}</span>
            </div>
          </div>
        ) : (
          <div className="flex items-center gap-4">
            <div className="flex items-center gap-1">
              <MapPin className="h-4 w-4 text-blue-500" />
              <span>Total Regions: {regionalData.length}</span>
            </div>
            <div className="flex items-center gap-1">
              <Users className="h-4 w-4 text-green-500" />
              <span>Total Customers: {regionalData.reduce((sum, r) => sum + r.customers, 0).toLocaleString()}</span>
            </div>
            <div className="flex items-center gap-1">
              <AlertTriangle className="h-4 w-4 text-orange-500" />
              <span>Avg Satisfaction: {(regionalData.reduce((sum, r) => sum + r.satisfaction, 0) / regionalData.length).toFixed(1)}/5</span>
            </div>
          </div>
        )}
        <div className="text-xs text-muted-foreground">
          {viewMode === "heatmap" ? `${dataType.charAt(0).toUpperCase() + dataType.slice(1)} Pattern` : "Geographic Overview"}
        </div>
      </div>
    </div>
  );
}

client/src/components/charts/performance.content:
import { useState } from "react";
import {
  ResponsiveContainer,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  Legend,
  Tooltip,
  ScatterChart,
  Scatter,
  XAxis,
  YAxis,
  CartesianGrid,
  Cell,
} from "recharts";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Activity, Zap, Shield } from "lucide-react";

interface PerformanceChartProps {
  title: string;
}

const generatePerformanceData = () => {
  return [
    {
      metric: 'Network Uptime',
      current: 99.2 + Math.random() * 0.7,
      target: 99.5,
      industry: 98.8,
    },
    {
      metric: 'Response Time',
      current: 92 + Math.random() * 6,
      target: 95,
      industry: 85,
    },
    {
      metric: 'Customer Satisfaction',
      current: 88 + Math.random() * 8,
      target: 90,
      industry: 82,
    },
    {
      metric: 'Issue Resolution',
      current: 94 + Math.random() * 4,
      target: 95,
      industry: 87,
    },
    {
      metric: 'Engineer Efficiency',
      current: 89 + Math.random() * 7,
      target: 92,
      industry: 83,
    },
    {
      metric: 'System Reliability',
      current: 96 + Math.random() * 3,
      target: 98,
      industry: 91,
    },
  ];
};

const generateScatterData = () => {
  const regions = ['North', 'South', 'East', 'West', 'Central'];
  const colors = ['#3b82f6', '#10b981', '#f59e0b', '#ef4444', '#8b5cf6'];

  return regions.map((region, index) => ({
    region,
    color: colors[index],
    data: Array.from({ length: 15 }, (_, i) => ({
      satisfaction: 75 + Math.random() * 20,
      responseTime: 0.5 + Math.random() * 2.5,
      customers: 50 + Math.random() * 200,
    }))
  }));
};

const generateEngineersData = () => {
  return Array.from({ length: 20 }, (_, i) => ({
    name: `Engineer ${i + 1}`,
    efficiency: 70 + Math.random() * 25,
    satisfaction: 75 + Math.random() * 20,
    issues: Math.floor(Math.random() * 50) + 10,
    specialization: ['Network', 'Hardware', 'Software', 'Installation'][Math.floor(Math.random() * 4)],
  }));
};

export function PerformanceChart({ title }: PerformanceChartProps) {
  const [chartType, setChartType] = useState("radar");
  const [dataSource, setDataSource] = useState("performance");
  const [isLoading, setIsLoading] = useState(false);

  const performanceData = generatePerformanceData();
  const scatterData = generateScatterData();
  const engineersData = generateEngineersData();

  const handleChartTypeChange = (newType: string) => {
    setIsLoading(true);
    setChartType(newType);
    setTimeout(() => setIsLoading(false), 200);
  };

  const handleDataSourceChange = (newSource: string) => {
    setIsLoading(true);
    setDataSource(newSource);
    setTimeout(() => setIsLoading(false), 300);
  };

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-card border border-border rounded-lg p-3 shadow-lg">
          <p className="text-foreground font-medium">{label}</p>
          {payload.map((entry: any, index: number) => (
            <p key={index} className="text-sm" style={{ color: entry.color }}>
              {entry.name}: {typeof entry.value === 'number' ? entry.value.toFixed(1) : entry.value}
              {entry.name.includes('Time') ? 'h' : entry.name.includes('Satisfaction') || entry.name.includes('Uptime') ? '%' : ''}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  const ScatterTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="bg-card border border-border rounded-lg p-3 shadow-lg">
          <p className="text-foreground font-medium">Region Analysis</p>
          <p className="text-sm text-blue-500">Satisfaction: {data.satisfaction.toFixed(1)}%</p>
          <p className="text-sm text-green-500">Response Time: {data.responseTime.toFixed(1)}h</p>
          <p className="text-sm text-purple-500">Customers: {data.customers.toFixed(0)}</p>
        </div>
      );
    }
    return null;
  };

  const renderChart = () => {
    if (dataSource === "regions" && chartType === "scatter") {
      return (
        <ScatterChart margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
          <XAxis 
            type="number" 
            dataKey="satisfaction" 
            name="Satisfaction" 
            unit="%" 
            stroke="hsl(var(--muted-foreground))" 
            fontSize={12}
            domain={[70, 100]}
          />
          <YAxis 
            type="number" 
            dataKey="responseTime" 
            name="Response Time" 
            unit="h" 
            stroke="hsl(var(--muted-foreground))" 
            fontSize={12}
            domain={[0, 3]}
          />
          <Tooltip content={<ScatterTooltip />} />
          <Legend />
          {scatterData.map((regionData, index) => (
            <Scatter
              key={regionData.region}
              name={regionData.region}
              data={regionData.data}
              fill={regionData.color}
            />
          ))}
        </ScatterChart>
      );
    }

    if (dataSource === "engineers") {
      return (
        <ScatterChart data={engineersData} margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
          <XAxis 
            dataKey="efficiency" 
            name="Efficiency" 
            unit="%" 
            stroke="hsl(var(--muted-foreground))" 
            fontSize={12}
          />
          <YAxis 
            dataKey="satisfaction" 
            name="Customer Satisfaction" 
            unit="%" 
            stroke="hsl(var(--muted-foreground))" 
            fontSize={12}
          />
          <Tooltip content={<CustomTooltip />} />
          <Scatter dataKey="issues" fill="#3b82f6" name="Engineers">
            {engineersData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={
                entry.specialization === 'Network' ? '#3b82f6' :
                entry.specialization === 'Hardware' ? '#10b981' :
                entry.specialization === 'Software' ? '#f59e0b' : '#ef4444'
              } />
            ))}
          </Scatter>
        </ScatterChart>
      );
    }

    // Default radar chart for performance data
    return (
      <RadarChart data={performanceData} margin={{ top: 20, right: 20, bottom: 20, left: 20 }}>
        <PolarGrid stroke="hsl(var(--border))" />
        <PolarAngleAxis dataKey="metric" tick={{ fontSize: 12, fill: 'hsl(var(--muted-foreground))' }} />
        <PolarRadiusAxis 
          angle={90} 
          domain={[0, 100]} 
          tick={{ fontSize: 10, fill: 'hsl(var(--muted-foreground))' }}
        />
        <Radar
          name="Current Performance"
          dataKey="current"
          stroke="#3b82f6"
          fill="#3b82f6"
          fillOpacity={0.2}
          strokeWidth={2}
        />
        <Radar
          name="Target"
          dataKey="target"
          stroke="#10b981"
          fill="#10b981"
          fillOpacity={0.1}
          strokeWidth={2}
          strokeDasharray="5 5"
        />
        <Radar
          name="Industry Average"
          dataKey="industry"
          stroke="#f59e0b"
          fill="#f59e0b"
          fillOpacity={0.05}
          strokeWidth={1}
          strokeDasharray="2 2"
        />
        <Legend />
        <Tooltip content={<CustomTooltip />} />
      </RadarChart>
    );
  };

  const getCurrentMetrics = () => {
    if (dataSource === "performance") {
      const avgCurrent = performanceData.reduce((sum, item) => sum + item.current, 0) / performanceData.length;
      const avgTarget = performanceData.reduce((sum, item) => sum + item.target, 0) / performanceData.length;
      const avgIndustry = performanceData.reduce((sum, item) => sum + item.industry, 0) / performanceData.length;

      return {
        current: avgCurrent.toFixed(1),
        target: avgTarget.toFixed(1),
        industry: avgIndustry.toFixed(1),
        gap: (avgCurrent - avgTarget).toFixed(1)
      };
    } else if (dataSource === "engineers") {
      const avgEfficiency = engineersData.reduce((sum, item) => sum + item.efficiency, 0) / engineersData.length;
      const avgSatisfaction = engineersData.reduce((sum, item) => sum + item.satisfaction, 0) / engineersData.length;
      const topPerformer = engineersData.reduce((prev, current) => 
        (prev.efficiency + prev.satisfaction) > (current.efficiency + current.satisfaction) ? prev : current
      );

      return {
        efficiency: avgEfficiency.toFixed(1),
        satisfaction: avgSatisfaction.toFixed(1),
        topPerformer: topPerformer.name,
        totalEngineers: engineersData.length
      };
    } else {
      const allData = scatterData.flatMap(region => region.data);
      const avgSatisfaction = allData.reduce((sum, item) => sum + item.satisfaction, 0) / allData.length;
      const avgResponseTime = allData.reduce((sum, item) => sum + item.responseTime, 0) / allData.length;
      const totalCustomers = allData.reduce((sum, item) => sum + item.customers, 0);

      return {
        satisfaction: avgSatisfaction.toFixed(1),
        responseTime: avgResponseTime.toFixed(1),
        customers: Math.round(totalCustomers),
        regions: scatterData.length
      };
    }
  };

  const metrics = getCurrentMetrics();

  return (
    <div className="w-full">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-foreground">{title}</h3>
        <div className="flex items-center gap-3">
          <Select value={dataSource} onValueChange={handleDataSourceChange} disabled={isLoading}>
            <SelectTrigger className="w-[140px] h-8 text-sm border-border/50 hover:border-border transition-colors">
              <SelectValue placeholder="Data source" />
            </SelectTrigger>
            <SelectContent className="min-w-[140px]">
              <SelectItem value="performance">Performance</SelectItem>
              <SelectItem value="engineers">Engineers</SelectItem>
              <SelectItem value="regions">Regions</SelectItem>
            </SelectContent>
          </Select>
          <Select value={chartType} onValueChange={handleChartTypeChange} disabled={isLoading}>
            <SelectTrigger className="w-[120px] h-8 text-sm border-border/50 hover:border-border transition-colors">
              <SelectValue placeholder="Chart type" />
            </SelectTrigger>
            <SelectContent className="min-w-[120px]">
              <SelectItem value="radar">Radar Chart</SelectItem>
              <SelectItem value="scatter">Scatter Plot</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="h-96 w-full relative">
        {isLoading && (
          <div className="absolute inset-0 bg-background/50 flex items-center justify-center z-10">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin"></div>
              <span className="text-sm text-muted-foreground">Loading...</span>
            </div>
          </div>
        )}
        <ResponsiveContainer width="100%" height="100%">
          {renderChart()}
        </ResponsiveContainer>
      </div>

      {/* Metrics Summary */}
      <div className="mt-4 flex items-center justify-between text-sm text-muted-foreground">
        <div className="flex items-center gap-4">
          {dataSource === "performance" && (
            <>
              <div className="flex items-center gap-1">
                <Activity className="h-4 w-4 text-blue-500" />
                <span>Avg Performance: {metrics.current}%</span>
              </div>
              <div className="flex items-center gap-1">
                <Zap className="h-4 w-4 text-green-500" />
                <span>Target Gap: {metrics.gap}%</span>
              </div>
              <div className="flex items-center gap-1">
                <Shield className="h-4 w-4 text-orange-500" />
                <span>vs Industry: +{(Number(metrics.current) - Number(metrics.industry)).toFixed(1)}%</span>
              </div>
            </>
          )}
          {dataSource === "engineers" && (
            <>
              <div className="flex items-center gap-1">
                <Activity className="h-4 w-4 text-blue-500" />
                <span>Avg Efficiency: {metrics.efficiency}%</span>
              </div>
              <div className="flex items-center gap-1">
                <Zap className="h-4 w-4 text-green-500" />
                <span>Satisfaction: {metrics.satisfaction}%</span>
              </div>
              <div className="flex items-center gap-1">
                <Shield className="h-4 w-4 text-purple-500" />
                <span>Top: {metrics.topPerformer}</span>
              </div>
            </>
          )}
          {dataSource === "regions" && (
            <>
              <div className="flex items-center gap-1">
                <Activity className="h-4 w-4 text-blue-500" />
                <span>Avg Satisfaction: {metrics.satisfaction}%</span>
              </div>
              <div className="flex items-center gap-1">
                <Zap className="h-4 w-4 text-green-500" />
                <span>Response Time: {metrics.responseTime}h</span>
              </div>
              <div className="flex items-center gap-1">
                <Shield className="h-4 w-4 text-purple-500" />
                <span>{metrics.regions} Regions</span>
              </div>
            </>
          )}
        </div>
        <div className="text-xs text-muted-foreground">
          {dataSource === "performance" ? "Multi-KPI Analysis" : 
           dataSource === "engineers" ? "Team Performance" : "Geographic Analysis"}
        </div>
      </div>
    </div>
  );
}

client/src/components/charts/revenue-cha.content:
import { useState } from "react";
import {
  ResponsiveContainer,
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  AreaChart,
  Area,
  ComposedChart,
  Bar,
} from "recharts";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { TrendingUp, DollarSign, CreditCard } from "lucide-react";

interface RevenueChartProps {
  title: string;
}

const generateRevenueData = (months: number) => {
  const data = [];
  const baseRevenue = 125000;
  const baseCustomers = 1200;

  for (let i = months; i >= 0; i--) {
    const date = new Date();
    date.setMonth(date.getMonth() - i);

    // Add growth trend
    const growthFactor = (months - i) / months;
    const seasonalVariance = Math.sin((date.getMonth() / 12) * 2 * Math.PI) * 0.1;

    const revenue = baseRevenue + (growthFactor * 50000) + (Math.random() - 0.5) * 20000 + (seasonalVariance * 15000);
    const customers = baseCustomers + (growthFactor * 300) + Math.floor((Math.random() - 0.5) * 100);
    const churn = 2 + Math.random() * 3;
    const arpu = revenue / customers;

    data.push({
      month: date.toLocaleDateString('en-US', { month: 'short', year: '2-digit' }),
      revenue: Math.round(revenue),
      customers,
      churnRate: Number(churn.toFixed(1)),
      arpu: Math.round(arpu),
      newCustomers: Math.floor(customers * 0.08 + Math.random() * 20),
    });
  }

  return data.reverse();
};

export function RevenueChart({ title }: RevenueChartProps) {
  const [timeRange, setTimeRange] = useState("12");
  const [metric, setMetric] = useState("revenue");
  const [isLoading, setIsLoading] = useState(false);

  const chartData = generateRevenueData(parseInt(timeRange));

  const handleTimeRangeChange = (newRange: string) => {
    setIsLoading(true);
    setTimeRange(newRange);
    setTimeout(() => setIsLoading(false), 300);
  };

  const handleMetricChange = (newMetric: string) => {
    setIsLoading(true);
    setMetric(newMetric);
    setTimeout(() => setIsLoading(false), 200);
  };

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-card border border-border rounded-lg p-3 shadow-lg">
          <p className="text-foreground font-medium">{label}</p>
          {payload.map((entry: any, index: number) => (
            <p key={index} className="text-sm" style={{ color: entry.color }}>
              {entry.name}: {
                entry.dataKey === 'revenue' ? `$${entry.value.toLocaleString()}` :
                entry.dataKey === 'churnRate' ? `${entry.value}%` :
                entry.dataKey === 'arpu' ? `$${entry.value}` :
                entry.value.toLocaleString()
              }
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  const getMetricConfig = () => {
    switch(metric) {
      case "revenue":
        return {
          dataKey: "revenue",
          color: "#10b981",
          name: "Revenue ($)",
          format: (value: number) => `$${value.toLocaleString()}`
        };
      case "customers":
        return {
          dataKey: "customers", 
          color: "#3b82f6",
          name: "Active Customers",
          format: (value: number) => value.toLocaleString()
        };
      case "churn":
        return {
          dataKey: "churnRate",
          color: "#ef4444", 
          name: "Churn Rate (%)",
          format: (value: number) => `${value}%`
        };
      case "arpu":
        return {
          dataKey: "arpu",
          color: "#f59e0b",
          name: "ARPU ($)",
          format: (value: number) => `$${value}`
        };
      default:
        return {
          dataKey: "revenue",
          color: "#10b981",
          name: "Revenue ($)",
          format: (value: number) => `$${value.toLocaleString()}`
        };
    }
  };

  const metricConfig = getMetricConfig();

  const renderChart = () => {
    if (metric === "combined") {
      return (
        <ComposedChart data={chartData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
          <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
          <XAxis dataKey="month" stroke="hsl(var(--muted-foreground))" fontSize={12} />
          <YAxis yAxisId="left" stroke="hsl(var(--muted-foreground))" fontSize={12} />
          <YAxis yAxisId="right" orientation="right" stroke="hsl(var(--muted-foreground))" fontSize={12} />
          <Tooltip content={<CustomTooltip />} />
          <Legend />
          <Area 
            yAxisId="left"
            type="monotone" 
            dataKey="revenue" 
            fill="rgba(16, 185, 129, 0.1)"
            stroke="#10b981"
            name="Revenue ($)"
          />
          <Bar yAxisId="right" dataKey="newCustomers" fill="#3b82f6" name="New Customers" />
          <Line 
            yAxisId="right"
            type="monotone" 
            dataKey="churnRate" 
            stroke="#ef4444" 
            strokeWidth={2}
            name="Churn Rate (%)"
            dot={{ fill: "#ef4444", strokeWidth: 2 }}
          />
        </ComposedChart>
      );
    }

    return (
      <AreaChart data={chartData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
        <XAxis dataKey="month" stroke="hsl(var(--muted-foreground))" fontSize={12} />
        <YAxis stroke="hsl(var(--muted-foreground))" fontSize={12} />
        <Tooltip content={<CustomTooltip />} />
        <Legend />
        <Area 
          type="monotone" 
          dataKey={metricConfig.dataKey} 
          stroke={metricConfig.color}
          fill={`${metricConfig.color}20`}
          name={metricConfig.name}
          strokeWidth={2}
        />
      </AreaChart>
    );
  };

  const currentValue = chartData.length > 0 ? chartData[chartData.length - 1][metricConfig.dataKey] : 0;
  const previousValue = chartData.length > 1 ? chartData[chartData.length - 2][metricConfig.dataKey] : 0;
  const changePercent = previousValue ? ((currentValue - previousValue) / previousValue * 100).toFixed(1) : "0";

  return (
    <div className="w-full">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-foreground">{title}</h3>
        <div className="flex items-center gap-3">
          <Select value={timeRange} onValueChange={handleTimeRangeChange} disabled={isLoading}>
            <SelectTrigger className="w-[140px] h-8 text-sm border-border/50 hover:border-border transition-colors">
              <SelectValue placeholder="Select range" />
            </SelectTrigger>
            <SelectContent className="min-w-[140px]">
              <SelectItem value="6">Last 6 months</SelectItem>
              <SelectItem value="12">Last 12 months</SelectItem>
              <SelectItem value="24">Last 24 months</SelectItem>
            </SelectContent>
          </Select>
          <Select value={metric} onValueChange={handleMetricChange} disabled={isLoading}>
            <SelectTrigger className="w-[140px] h-8 text-sm border-border/50 hover:border-border transition-colors">
              <SelectValue placeholder="Select metric" />
            </SelectTrigger>
            <SelectContent className="min-w-[140px]">
              <SelectItem value="revenue">Revenue</SelectItem>
              <SelectItem value="customers">Customers</SelectItem>
              <SelectItem value="churn">Churn Rate</SelectItem>
              <SelectItem value="arpu">ARPU</SelectItem>
              <SelectItem value="combined">Combined View</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="h-80 w-full relative">
        {isLoading && (
          <div className="absolute inset-0 bg-background/50 flex items-center justify-center z-10">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin"></div>
              <span className="text-sm text-muted-foreground">Loading...</span>
            </div>
          </div>
        )}
        <ResponsiveContainer width="100%" height="100%">
          {renderChart()}
        </ResponsiveContainer>
      </div>

      {/* Chart Summary */}
      <div className="mt-4 flex items-center justify-between text-sm text-muted-foreground">
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-1">
            <TrendingUp className="h-4 w-4 text-green-500" />
            <span>Current: {metricConfig.format(currentValue)}</span>
          </div>
          <div className="flex items-center gap-1">
            <DollarSign className="h-4 w-4 text-blue-500" />
            <span>Change: {Number(changePercent) >= 0 ? '+' : ''}{changePercent}%</span>
          </div>
          <div className="flex items-center gap-1">
            <CreditCard className="h-4 w-4 text-purple-500" />
            <span>Avg: {metricConfig.format(chartData.reduce((sum, item) => sum + item[metricConfig.dataKey], 0) / chartData.length)}</span>
          </div>
        </div>
        <div className="text-xs text-muted-foreground">
          {metric === "combined" ? "Multi-metric" : metricConfig.name} - {timeRange} months
        </div>
      </div>
    </div>
  );
}

client/src/components/charts/status-char.content:
import { useState } from "react";
import {
  ResponsiveContainer,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  PieChart,
  Pie,
  Cell,
} from "recharts";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";

interface StatusChartProps {
  title: string;
  data?: any[];
}

// Generate realistic status data
const generateStatusData = (days: number) => {
  const data = [];
  // Adjust sample frequency based on time range
  const sampleFrequency = days <= 7 ? 1 : days <= 30 ? 2 : 7;

  for (let i = days; i >= 0; i -= sampleFrequency) {
    const date = new Date();
    date.setDate(date.getDate() - i);

    // Add trending - more issues in the past, improving over time
    const trendFactor = i / days;
    const basePending = 2 + Math.floor(trendFactor * 4);
    const baseAssigned = 3 + Math.floor(trendFactor * 6);
    const baseInProgress = 4 + Math.floor(trendFactor * 5);
    const baseResolved = 8 + Math.floor(trendFactor * 10);

    data.push({
      date: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
      pending: Math.floor(Math.random() * 8) + basePending,
      assigned: Math.floor(Math.random() * 12) + baseAssigned,
      inProgress: Math.floor(Math.random() * 10) + baseInProgress,
      resolved: Math.floor(Math.random() * 20) + baseResolved,
    });
  }

  return data.reverse(); // Show chronological order
};

const getCurrentStatusDistribution = (timeRange: string) => {
  const multiplier = timeRange === "7" ? 1 : timeRange === "30" ? 4 : 12;
  const baseValues = [
    { name: 'Pending', value: 14, color: '#ef4444' },
    { name: 'Assigned', value: 28, color: '#f59e0b' },
    { name: 'In Progress', value: 31, color: '#3b82f6' },
    { name: 'Resolved', value: 142, color: '#10b981' },
  ];

  return baseValues.map(item => ({
    ...item,
    value: Math.floor(item.value * multiplier + (Math.random() - 0.5) * 10)
  }));
};

export function StatusChart({ title, data = [] }: StatusChartProps) {
  const [timeRange, setTimeRange] = useState("7");
  const [chartType, setChartType] = useState<"bar" | "pie">("bar");
  const [isLoading, setIsLoading] = useState(false);

  const chartData = generateStatusData(parseInt(timeRange));
  const pieData = getCurrentStatusDistribution(timeRange);

  const handleTimeRangeChange = (newRange: string) => {
    setIsLoading(true);
    setTimeRange(newRange);
    setTimeout(() => setIsLoading(false), 300);
  };

  const handleChartTypeChange = (newType: string) => {
    setIsLoading(true);
    setChartType(newType as "bar" | "pie");
    setTimeout(() => setIsLoading(false), 200);
  };

  const CustomTooltip = ({ active, payload, label }: any) => {
    if (active && payload && payload.length) {
      return (
        <div className="bg-card border border-border rounded-lg p-3 shadow-lg">
          <p className="text-foreground font-medium">{label}</p>
          {payload.map((entry: any, index: number) => (
            <p key={index} className="text-sm" style={{ color: entry.color }}>
              {entry.name}: {entry.value}
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  const PieTooltip = ({ active, payload }: any) => {
    if (active && payload && payload.length) {
      const data = payload[0];
      return (
        <div className="bg-card border border-border rounded-lg p-3 shadow-lg">
          <p className="text-foreground font-medium">{data.name}</p>
          <p className="text-sm" style={{ color: data.payload.color }}>
            Count: {data.value}
          </p>
          <p className="text-sm text-muted-foreground">
            {((data.value / pieData.reduce((sum, item) => sum + item.value, 0)) * 100).toFixed(1)}%
          </p>
        </div>
      );
    }
    return null;
  };

  const renderChart = () => {
    if (chartType === "pie") {
      return (
        <PieChart>
          <Pie
            data={pieData}
            cx="50%"
            cy="50%"
            innerRadius={60}
            outerRadius={100}
            paddingAngle={5}
            dataKey="value"
          >
            {pieData.map((entry, index) => (
              <Cell key={`cell-${index}`} fill={entry.color} />
            ))}
          </Pie>
          <Tooltip content={<PieTooltip />} />
          <Legend 
            formatter={(value) => <span className="text-foreground">{value}</span>}
          />
        </PieChart>
      );
    }

    return (
      <BarChart data={chartData} margin={{ top: 5, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" stroke="hsl(var(--border))" />
        <XAxis dataKey="date" stroke="hsl(var(--muted-foreground))" fontSize={12} />
        <YAxis stroke="hsl(var(--muted-foreground))" fontSize={12} />
        <Tooltip content={<CustomTooltip />} />
        <Legend />
        <Bar dataKey="pending" fill="#ef4444" name="Pending" />
        <Bar dataKey="assigned" fill="#f59e0b" name="Assigned" />
        <Bar dataKey="inProgress" fill="#3b82f6" name="In Progress" />
        <Bar dataKey="resolved" fill="#10b981" name="Resolved" />
      </BarChart>
    );
  };

  return (
    <div className="w-full">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold text-foreground">{title}</h3>
        <div className="flex items-center gap-3">
          <Select value={timeRange} onValueChange={handleTimeRangeChange} disabled={isLoading}>
            <SelectTrigger className="w-[140px] h-8 text-sm border-border/50 hover:border-border transition-colors">
              <SelectValue placeholder="Select range" />
            </SelectTrigger>
            <SelectContent className="min-w-[140px]">
              <SelectItem value="7">Last 7 days</SelectItem>
              <SelectItem value="30">Last 30 days</SelectItem>
              <SelectItem value="90">Last 3 months</SelectItem>
            </SelectContent>
          </Select>
          <Select value={chartType} onValueChange={handleChartTypeChange} disabled={isLoading}>
            <SelectTrigger className="w-[120px] h-8 text-sm border-border/50 hover:border-border transition-colors">
              <SelectValue placeholder="Chart type" />
            </SelectTrigger>
            <SelectContent className="min-w-[120px]">
              <SelectItem value="bar">Bar Chart</SelectItem>
              <SelectItem value="pie">Pie Chart</SelectItem>
            </SelectContent>
          </Select>
        </div>
      </div>

      <div className="h-64 w-full relative">
        {isLoading && (
          <div className="absolute inset-0 bg-background/50 flex items-center justify-center z-10">
            <div className="flex items-center gap-2">
              <div className="w-4 h-4 border-2 border-primary border-t-transparent rounded-full animate-spin"></div>
              <span className="text-sm text-muted-foreground">Loading...</span>
            </div>
          </div>
        )}
        <ResponsiveContainer width="100%" height="100%">
          {renderChart()}
        </ResponsiveContainer>
      </div>

      {/* Chart Summary */}
      <div className="mt-4 flex items-center justify-between text-sm text-muted-foreground">
        <div className="flex items-center gap-4">
          <div className="flex items-center gap-1">
            <div className="w-2 h-2 bg-green-500 rounded-full"></div>
            <span>Resolution Rate: {pieData.length > 0 ? (pieData.find(item => item.name === 'Resolved')?.value || 0) / pieData.reduce((sum, item) => sum + item.value, 0) * 100 : 0}%</span>
          </div>
          <div className="flex items-center gap-1">
            <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
            <span>Total Issues: {pieData.reduce((sum, item) => sum + item.value, 0)}</span>
          </div>
          <div className="flex items-center gap-1">
            <div className="w-2 h-2 bg-red-500 rounded-full"></div>
            <span>Pending: {pieData.find(item => item.name === 'Pending')?.value || 0}</span>
          </div>
        </div>
        <div className="text-xs text-muted-foreground">
          {chartType === "pie" ? "Distribution" : "Trends"} - {timeRange} days
        </div>
      </div>
    </div>
  );
}

