    }
  ];

  const unreadCount = notifications.filter(n => n.unread).length;

  const getRoleDisplayName = (role: string) => {
    switch (role) {
      case "super-admin":
        return "Super Admin";
      case "admin":
        return "Admin";
      case "manager":
        return "Manager";
      default:
        return role;
    }
  };

  const getUserInitials = (email: string) => {
    return email.split('@')[0].substring(0, 2).toUpperCase();
  };

  const getNotificationIcon = (type: string) => {
    switch (type) {
      case "complaint":
        return "";
      case "system":
        return "";
      case "update":
        return "";
      case "payment":
        return "";
      default:
        return "";
    }
  };

  return (
    <header 
      className="backdrop-blur-md border-b sticky top-0 z-50 transition-all duration-300"
      style={{
        background: "var(--header-bg)",
        borderColor: "var(--header-border)"
      }}
    >
      <div className="flex items-center justify-between px-4 sm:px-6 py-3 sm:py-4">
        {/* Left Section */}
        <div className="flex items-center flex-1">
          <Button 
            variant="ghost" 
            size="icon" 
            className="lg:hidden mr-2 sm:mr-4 transition-all duration-200"
            style={{
              color: "var(--header-icon)",
            }}
            onMouseEnter={(e) => e.currentTarget.style.color = "var(--header-icon-hover)"}
            onMouseLeave={(e) => e.currentTarget.style.color = "var(--header-icon)"}
            onClick={onMenuClick}
          >
            <Menu className="h-6 w-6" />
          </Button>
          <h1 
            className="text-lg sm:text-xl lg:text-2xl font-bold tracking-tight mr-4 transition-colors duration-200"
            style={{ color: "var(--header-text)" }}
          >
            {title}
          </h1>

          {/* Global Search - Hidden on small screens */}
          <div className="hidden md:flex items-center flex-1 max-w-md ml-4">
            <GlobalSearch />
          </div>
        </div>

        {/* Right Section */}
        <div className="flex items-center space-x-2 sm:space-x-4">
          {/* Search Button - Mobile Only */}
          <Button 
            variant="ghost" 
            size="icon" 
            className="md:hidden transition-all duration-200"
            style={{ color: "var(--header-icon)" }}
            onMouseEnter={(e) => e.currentTarget.style.color = "var(--header-icon-hover)"}
            onMouseLeave={(e) => e.currentTarget.style.color = "var(--header-icon)"}
          >
            <Search className="h-5 w-5" />
          </Button>

          {/* Notifications */}
          <Popover>
            <PopoverTrigger asChild>
              <Button 
                variant="ghost" 
                size="icon" 
                className="relative transition-all duration-200"
                style={{ color: "var(--header-icon)" }}
                onMouseEnter={(e) => e.currentTarget.style.color = "var(--header-icon-hover)"}
                onMouseLeave={(e) => e.currentTarget.style.color = "var(--header-icon)"}
              >
                <Bell className="h-5 w-5" />
                {unreadCount > 0 && (
                  <Badge 
                    variant="destructive" 
                    className="absolute -top-1 -right-1 h-5 w-5 p-0 flex items-center justify-center text-xs animate-pulse"
                  >
                    {unreadCount}
                  </Badge>
                )}
              </Button>
            </PopoverTrigger>
            <PopoverContent 
              className="w-80 p-0 shadow-xl border-0" 
              align="end"
              style={{
                background: "var(--dropdown-bg)",
